<script>
import { mapState } from 'vuex'
export default {
  name: 'render',
  computed: mapState({ user: state => state.user }),
  created () { window.addEventListener('scroll', this.viewFixed) },
  destroyed () { window.removeEventListener('scroll', this.viewFixed) },
  data () {
    return {
      viewfixed: false
    }
  },
  methods: {
    rotateFigure () {
      let photo = document.querySelector('#photo'),
          transform = photo.style.transform

      switch (transform) {
        case 'rotate(90deg)':
          photo.style.transform = "rotate(180deg)"
          break;
        case 'rotate(180deg)':
          photo.style.transform = "rotate(270deg)"
          break;
        case 'rotate(270deg)':
          photo.style.transform = ""
          break;
        default:
          photo.style.transform = "rotate(90deg)"
          break;
      }
    },
    viewFixed () {
      if (window.innerWidth >= 800) {
        let target = document.querySelector('#curriculo').getBoundingClientRect()
        this.viewfixed = target.y <= 0
      } else {
        this.viewfixed = false
      }
    },
    translateDate (date) {
      if (!date) return ''

      date = date.split("-")

      switch (date[1]) {
        case "01":
          date[1] = this.$t('calendar.january')
          break
        case "02":
          date[1] = this.$t('calendar.february')
          break
        case "03":
          date[1] = this.$t('calendar.march')
          break
        case "04":
          date[1] = this.$t('calendar.april')
          break
        case "05":
          date[1] = this.$t('calendar.may')
          break
        case "06":
          date[1] = this.$t('calendar.june')
          break
        case "07":
          date[1] = this.$t('calendar.july')
          break
        case "08":
          date[1] = this.$t('calendar.august')
          break
        case "09":
          date[1] = this.$t('calendar.september')
          break
        case "10":
          date[1] = this.$t('calendar.october')
          break
        case "11":
          date[1] = this.$t('calendar.november')
          break
        case "12":
          date[1] = this.$t('calendar.december')
          break
      }

      return `${date[1]} ${this.$t('calendar.in')} ${date[0]}`
    }
  }
}
</script>
